package twoPointer;

import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;
/*
{a=141, b=131, c=135, d=140, e=139, f=131, g=121, h=128, i=144, j=128, k=138, l=138, m=139, n=133, o=136, p=153, q=115, r=126, s=124, t=128, u=126, v=138, w=126, x=138, y=148, z=122}
 */
public class LongestSubstringWithoutRepeatCharacters {
    static class Solution {
        public boolean checkInclusion(String s1, String s2) {
            Map<Character, Integer> s1Map = new HashMap<>();
            s1.chars().forEach(e -> s1Map.put((char) e, s1Map.getOrDefault((char)e, 0) + 1));
            int i = 0, inclusionArrLength = s1.length();
            while (i <= s2.length() - inclusionArrLength) {
                Map<Character, Integer> s2Map = new HashMap<>();
                s2.substring(i, i + inclusionArrLength).chars().forEach(e -> s2Map.put((char) e, s2Map.getOrDefault((char)e, 0) + 1));
                if(s2Map.size() == s1Map.size()) {
                    for (Map.Entry m :
                            s1Map.entrySet()) {
                        if (s2Map.containsKey(m.getKey()) && s2Map.get(m.getKey()).equals(m.getValue())) {
                            s2Map.remove(m.getKey());
                        }
                    }
                    if (s2Map.isEmpty()) {
                        return true;
                    }
                }
                i++;
            }
            return false;
        }
    }
    public static void main (String[] args) {
        String s1 = "";
        String s2  = "";
        long beforeUsedMem=Runtime.getRuntime().totalMemory()-Runtime.getRuntime().freeMemory();
//        System.out.println(beforeUsedMem);
        long startTime = System.nanoTime();
//        System.out.println(startTime);
        boolean isPresent = new Solution().checkInclusion(s1, s2);
        long endTime = System.nanoTime();
        long afterUsedMem=Runtime.getRuntime().totalMemory()-Runtime.getRuntime().freeMemory();
//        System.out.println(endTime);
//        System.out.println(afterUsedMem);
        System.out.println("isPresent:" + isPresent);
        System.out.println("\nTime Taken:" + (endTime - startTime)/1000000.0);
        System.out.println("Memory Used:" + (afterUsedMem-beforeUsedMem));
    }
}
